    private void loginbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginbtnActionPerformed
        String nomeUsuario = usertxt.getText();
        String senha = new String(userpass.getPassword());

        Usuario usuario = PersistenciaUser.autenticarUsuario(nomeUsuario, senha);

        if (usuario != null) {
            JOptionPane.showMessageDialog(this, "Login bem-sucedido! Bem-vindo, " + usuario.getNomeUsuario());
            new Main().setVisible(true);
            this.setVisible(false);
        } else {
            JOptionPane.showMessageDialog(this, "Usu√°rio ou senha incorretos.");
        }
    }//GEN-LAST:event_loginbtnActionPerformed

